mapper.select:
SELECT b.billid AS id, b.billnumber AS documentNo, CONVERT(VARCHAR(100), b.pdaInTime, 23) AS documentDate, 1 AS type, '验收单' AS typeDescription, CASE b.billstates WHEN 10 THEN '验收中' WHEN 13 THEN '已验收' ELSE '' END AS status FROM ${account}.dbo.pda_RecBill b LEFT JOIN ${account}.dbo.pda_RecBill_D d ON b.billid = d.bill_id WHERE b.pdastates != 2
AND d.s_id=#{sId}
ORDER BY documentDate DESC

SELECT b.billid AS id, b.billnumber AS documentNo, CONVERT(VARCHAR(100), b.pdaInTime, 23) AS documentDate, 3 AS type, '调拨单' AS typeDescription, CASE b.billstates WHEN 2 THEN '待调拨' WHEN 3 THEN '调拨中' ELSE '' END AS status FROM ${account}.dbo.pda_TranBill b WHERE b.pdastates != 2
AND b.sout_id=#{sId}
ORDER BY documentDate DESC

SELECT b.billid AS id, b.billnumber AS documentNo, CONVERT(VARCHAR(100), b.pdaInTime, 23) AS documentDate, 2 AS type, '上架单' AS typeDescription, CASE b.billstates WHEN 10 THEN '待上架' WHEN 13 THEN '已上架' ELSE '' END AS status FROM ${account}.dbo.pda_PutOnBill b WHERE b.pdastates != 2
AND d.s_id=#{sId}
ORDER BY documentDate DESC
